$(window).load(function() {

      $("#title").fadeIn(2000,'easeOutCirc');

      $("#intro").delay(1000).fadeIn(1000,'easeOutCirc');

      $("#btn-click").delay(2000).fadeIn(500,'easeOutCirc');

      setArrowAnimationDown();


    $("#btn-click").mouseover(function() {
        $("#btn-click").css("opacity", ".3");
    });

    $("#btn-click").mouseout(function() {
        $("#btn-click").css("opacity", "1");
    });

    $("#btn-click").click(function() {
        $("#btn-click").fadeOut(1000).css("display","none");
        $("#btn-click2").fadeIn(1000).css("display","block");
        $("#click").velocity({height: 'toggle'});
        $("#title").fadeOut(1000,'easeOutCirc');
        $("#intro").css("display","none");
        setArrowAnimationUp();
        setclickAnimation();
        setPic1Animation();
        setPic2Animation();
        setPic3Animation();
        setPic4Animation();
    });

    $("#btn-click2").mouseover(function() {
        $("#btn-click2").css("opacity", ".3");
    });

    $("#btn-click2").mouseout(function() {
        $("#btn-click2").css("opacity", "1");
    });

    $("#btn-click2").click(function() {
        setForceFadeOut();
        $("#title").fadeIn(600,'easeOutCirc');
        $("#intro").fadeIn(600,'easeOutCirc');
        $("#click2").velocity({height: 'toggle'});
        $("#btn-click").fadeIn(500).css("display","block");
        $("#btn-click2").fadeIn(500).css("display","none");
    });
});


function setArrowAnimationDownDown(){
        $("#btn-click img").delay(100).velocity({ bottom: '30px', opacity: '0.4' }, "slow");
        setArrowAnimationDownUp();
        if($("#btn-click").data("clicked",true)){
          return;}
};

function setArrowAnimationDownUp(){
        $("#btn-click img").velocity({ bottom: '50px', opacity: '0.8' }, "slow");
        setArrowAnimationDownDown();
};

function setclickAnimation(){
  $("#contents section h2").fadeIn(1000);
  $("#contents section #pic1").fadeIn(1000);
  $("#contents section #pic2").fadeIn(1000);
  $("#contents section #pic3").fadeIn(1000);
  $("#contents section #pic4").fadeIn(1000);
};

function setPic1Animation(){
  $("#contents section #pic1").mouseover(function() {
      $("#contents section #pic1").css("opacity", ".3");
  });

  $("#contents section #pic1").mouseout(function() {
      $("#contents section #pic1").css("opacity", "1");
  });

  $("#contents section #pic1").click(function(){
    $("#contents section img[id!='pic1']").fadeToggle(300);
    $("#contents #sec-information #sec1 dt").fadeToggle(500);
    $("#contents #sec-information #sec1 dd").fadeToggle(600);
  });
};

